import { Component } from 'react';
import './App.css';

import ErrorBoundary from './components/ErrorBoundary';
import Form from './components/Form';

const API_KEY = "cAnUXB4uhv2xyVzMZCEkwNO6FdRlpoIh";

class App extends Component {
  state = {
    books: []
  }

  getBook = async (e) => {
    const bookDate = e.target.elements.bookName.value;
    e.preventDefault();
    const api_call = await fetch (`https://api.nytimes.com/svc/books/v3/lists/${bookDate}/mass-market-paperback.json?api-key=${API_KEY}`);

    const data = await api_call.json();
    this.setState({ books: data.results.books })
    console.log(this.state.books);
  }

  render() {
    return (
      <div className="App">
        <h1>birthday book</h1>
            <h3>what were the NYT bestsellers on your birthday? (only from 2008)</h3>
          <ErrorBoundary>
          <Form getBook={this.getBook} />
          { this.state.books.map((book) => {
            return (
              <div>
                <img src={book.book_image} alt={book.title} />
                <p key={book.primary_isbn13}>{ book.title }</p>
              </div>)
          }) }
          </ErrorBoundary>
      </div>
    )
  }
}

export default App;
