import { Component } from 'react';
import './App.css';

import Form from './components/Form';

const API_KEY = "cAnUXB4uhv2xyVzMZCEkwNO6FdRlpoIh";

class App extends Component {

  getBook = async (e) => {
    const bookName = e.target.elements.bookName.value;
    e.preventDefault();
    const api_call = await fetch (`https://api.nytimes.com/svc/books/v3/lists/overview/2019-02-03.json?api-key=${API_KEY}`);

    const data = await api_call.json();
    // this.setState({ books: data.results.books })
    console.log(data);
  }

  render() {
    return (
      <div className="App">
        <h1>birthday book</h1>
          <h3>what were the NYT bestsellers when you were born?</h3>
          <Form getBook={this.getBook} />
      </div>
    )
  }
}

export default App;
